from lib.read_timer import *
import lib.read_timer
import pytest
def test_if_read_timer_exists():
    assert hasattr(lib.read_timer, 'read_timer')

def test_if_story_is_string():
    with pytest.raises(TypeError) as e:
        timer = read_timer(5, 10)
    error_message = str(e.value)
    assert error_message == "Please input a string for `story`"

def test_if_wpm_is_integer():
    with pytest.raises(TypeError) as e:
        timer = read_timer('hello','goodbye')
    error_message = str(e.value)
    assert error_message == "Please input an integer for `wpm`"


